function response_get_core_directory_uri() {
	echo get_template_directory_uri() . '/core';
}

// TODO: Fix documentation
// Enqueue core scripts and core styles
function response_core_scripts() {
	global $post;

	// Load Bootstrap Library Items
	wp_enqueue_style( 'bootstrap-styles', get_template_directory_uri() . '/core/lib/bootstrap/css/bootstrap.min.css', false, '2.0.4' );
	wp_enqueue_style( 'bootstrap-responsive-styles', get_template_directory_uri() . '/core/lib/bootstrap/css/bootstrap-responsive.min.css', array('bootstrap-styles'), '2.0.4' );
	wp_enqueue_script( 'bootstrap-js', get_template_directory_uri() . '/core/lib/bootstrap/js/bootstrap.min.js', array( 'jquery' ), '2.0.4', true );
	
	// Load Theme Stylesheet
	wp_enqueue_style( 'style', get_stylesheet_uri(), array('bootstrap-responsive-styles', 'bootstrap-styles'), '1.0' );
}
add_action( 'wp_enqueue_scripts', 'response_core_scripts', 20 );


