	register_sidebar( array(
		'name' => 'Footer Widgets',
		'id' => 'response_footer_widgets',
		'before_widget' => '<div class="span3">',
		'after_widget' => '</div>',
		'before_title' => '<h2>',
		'after_title' => '</h2>',
	) );
	
	// TODO: Update this code to be cleaner
	register_sidebar(array(
		'name' => __( 'Sidebar' ),
		'id' => 'sidebar',
		'description' => __( 'Widgets in this area will be shown on the right-hand side.' ),
		'before_widget' => '<div class="row-fluid">',
		'after_widget' => '</div>',
		'before_title' => '<h1>',
		'after_title' => '</h1>'
	));
}
add_action( 'widgets_init', 'response_footer_widgets_init' );


