function cyberchimps_init_carousel_post_type() {
	register_post_type( 'featured_posts',
		array(
			'labels' => array(
				'name' => __( 'Carousel', 'cyberchimps'),
				'singular_name' => __( 'Posts', 'cyberchimps'),
			),
			'public' => true,
			'show_ui' => true, 
			'supports' => array('custom-fields'),
			'taxonomies' => array( 'carousel_categories'),
			'has_archive' => true,
			'menu_icon' => get_template_directory_uri() . '/cyberchimps/lib/images/custom-types/carousel.png',
			'rewrite' => array('slug' => 'slides')
		)
	);
	
		$labels = array(
    'name' => _x( 'Carousel Categories', 'taxonomy general name' ),
    'singular_name' => _x( 'Carousel Catergory', 'taxonomy singular name' ),
    'search_items' =>  __( 'Search Carousel' ),
    'all_items' => __( 'All Carousels' ),
    'parent_item' => __( 'Carousel Category' ),
    'parent_item_colon' => __( 'Carousel Category:' ),
    'edit_item' => __( 'Edit Carousel Category' ), 
    'update_item' => __( 'Update Carousel Category' ),
    'add_new_item' => __( 'Add New Carousel Category' ),
    'new_item_name' => __( 'New Carousel Category Name' ),
    'menu_name' => __( 'Carousel Category' ),
  ); 	
	register_taxonomy( 'carousel_categories',array('featured_posts'), array(
		'public' => true,
		'show_in_nav_menus' => false,
    'hierarchical' => true,
    'labels' => $labels,
    'show_ui' => true
  ));
	
	$meta_boxes = array();
		
	$mb = new Chimps_Metabox('Carousel', 'Featured Post Carousel', array('pages' => array('featured_posts')));
	$mb
		->tab("Featured Post Carousel Options")
			->text('post_title', 'Featured Post Title', '')
			->single_image('post_image', 'Featured Post Image', '')
			->text('post_url', 'Featured Post URL', '')
			->reorder('reorder_id', 'Reorder Name', 'Reorder Desc' )
		->end();
}
add_action( 'init', 'cyberchimps_init_carousel_post_type' );