// FIXME: Fix documentation
add_filter( 'response_content_class', 'response_default_content_classes');
function response_default_content_classes( $classes ) {
	$classes[] = '';
	return $classes;
}

// FIXME: Fix documentation
function response_filter_content_class( ) {
	// Separates classes with a single space, collates classes for content element
	echo 'class="' . esc_attr( join( ' ', apply_filters( 'response_content_class', array() ) ) ) . '"';
}

